    and company_tax_no=#{companyTaxNo}


    and voucher_no=#{voucherNo}


    and account_status=#{accountStatus}


    and confirm_status=#{confirmStatus}


    and is_check in (${isCheck})


    and tax_period=#{taxPeriod}


    and bill_status=#{billStatus}


     and (un_check_time BETWEEN #{unCheckTimeStart} and #{unCheckTimeEnd}) 


    and contract_no=#{contractNo}


    and customs_doc_no=#{customsDocNo}


     and (check_time BETWEEN #{checkTimeStart} and #{checkTimeEnd}) 


     and (paper_drew_date BETWEEN #{paperDrewDateStart} and #{paperDrewDateEnd}) 


    and (voucher_account_time >= concat(#{voucherAccountTimeStart}, ' 00:00:01'))


    and (voucher_account_time <= concat(#{voucherAccountTimeEnd}, ' 23:59:59'))


