mapper.select:
		/**mycat:schema=${schemaLabel}*/
		select
		count(1)
 		from t_dx_tax_current
		where 1=1

			and taxno = #{map.taxNo}


			and declare_period = #{map.declarePeriod}


			and credit_rating = #{map.creditRating}

		AND taxno in (SELECT taxno FROM t_ac_org where orgid in(SELECT orgid FROM t_ac_user_taxno where userid=#{map.userId}))
		or  old_tax_no in (SELECT taxno FROM t_ac_org where orgid in(SELECT orgid FROM t_ac_user_taxno where userid=#{map.userId}))

/**mycat:schema=${schemaLabel}*/ select id ,taxno ,old_tax_no oldTaxNo ,taxname ,current_tax_period currentTaxPeriod ,select_start_date selectStartDate ,select_end_date selectEndDate ,operation_end_date operationEndDate ,credit_rating creditRating ,declare_period declarePeriod ,update_time updateTime ,create_time createTime from t_dx_tax_current where 1=1
and taxno = #{map.taxNo}
and declare_period = #{map.declarePeriod}
and credit_rating = #{map.creditRating}
AND taxno in (SELECT taxno FROM t_ac_org where orgid in(SELECT orgid FROM t_ac_user_taxno where userid=#{map.userId})) or old_tax_no in (SELECT taxno FROM t_ac_org where orgid in(SELECT orgid FROM t_ac_user_taxno where userid=#{map.userId}))
limit #{map.offset}, #{map.limit}

/**mycat:schema=${schemaLabel}*/ select id ,taxno ,old_tax_no oldTaxNo ,taxname ,current_tax_period currentTaxPeriod ,select_start_date selectStartDate ,select_end_date selectEndDate ,operation_end_date operationEndDate ,credit_rating creditRating ,declare_period declarePeriod ,update_time updateTime ,create_time createTime from t_dx_tax_current where 1=1
and taxno = #{map.taxNo}
and declare_period = #{map.declarePeriod}
and credit_rating = #{map.creditRating}
AND taxno in (SELECT taxno FROM t_ac_org where orgid in(SELECT orgid FROM t_ac_user_taxno where userid=#{map.userId})) or old_tax_no in (SELECT taxno FROM t_ac_org where orgid in(SELECT orgid FROM t_ac_user_taxno where userid=#{map.userId}))
