mapper.select:
SELECT * FROM t_dx_po_detail WITH(NOLOCK) where id in(
select code from t_dx_match_po_claim_invoice WITH(NOLOCK) where matchno=#{matchno} and code_type='1'
)

SELECT * FROM t_dx_return_goods WITH(NOLOCK) 
WHERE
matchno=#{matchno}

SELECT matchno from t_dx_po_detail WITH(NOLOCK) 
where receiptid=#{entity.receiptid}
AND  pocode=#{entity.pocode}
and traction_id=#{entity.tractionId}

select * from (SELECT pocode, max(receiptdate) receiptdate, SUM (receiptAmount) receiptAmount, SUM (amountpaid) amountpaid, SUM (amountunpaid) amountunpaid FROM t_dx_po_detail WITH(NOLOCK) WHERE dxhy_match_status in ('0','1','5','6') and host_status='1' and venderid=#{venderid} AND jvcode=#{jvcode} GROUP BY pocode) as A WHERE 1=1
AND A.pocode like concat('%',concat(#{pocode},'%'))
AND SUBSTRING( convert(varchar(100),A.receiptdate,20),0,11) >= #{poDateStart}
AND SUBSTRING( convert(varchar(100),A.receiptdate,20),0,11) <= #{poDateEnd}
ORDER BY A.receiptdate DESC

select count(*) from (SELECT pocode, max(receiptdate) receiptdate, SUM (receiptAmount) receiptAmount, SUM (amountpaid) amountpaid, SUM (amountunpaid) amountunpaid FROM t_dx_po_detail WITH(NOLOCK) WHERE dxhy_match_status in ('0','1','5','6') and host_status='1' and venderid=#{venderid} AND jvcode=#{jvcode} GROUP BY pocode) as A WHERE 1=1
AND A.pocode like concat('%',concat(#{pocode},'%'))
AND SUBSTRING( convert(varchar(100),A.receiptdate,20),0,11) >= #{poDateStart}
AND SUBSTRING( convert(varchar(100),A.receiptdate,20),0,11) <= #{poDateEnd}

SELECT * FROM t_dx_return_goods WITH(NOLOCK) 

    supplier_association=#{venderid}

    AND jvcode=#{jvcode}
    and match_status in ('0','1','5','6')
    and host_status='1'

       AND return_goods_code like concat('%',concat(#{claimno},'%'))


         AND SUBSTRING(convert(varchar(100),return_goods_date,20),0,11)  >= #{claimDateStart} 


         AND SUBSTRING(convert(varchar(100),return_goods_date,20),0,11)  <= #{claimDateEnd} 



order by return_goods_date

SELECT count(1) FROM t_dx_return_goods WITH(NOLOCK) 

    supplier_association=#{venderid}

    AND jvcode=#{jvcode}
    and match_status in ('0','1','5','6')
    and host_status='1'

        AND return_goods_code like concat('%',concat(#{claimno},'%'))


         AND SUBSTRING(convert(varchar(100),return_goods_date,20),0,11)  >= #{claimDateStart} 


         AND SUBSTRING(convert(varchar(100),return_goods_date,20),0,11)  <= #{claimDateEnd}

SELECT * FROM t_dx_record_invoice WITH(NOLOCK) 
WHERE
    uuid=#{uuid}

mapper.insert:
INSERT INTO t_dx_record_invoice WITH(ROWLOCK)( invoice_code, invoice_no, invoice_amount, invoice_date, total_amount, tax_amount, tax_rate, venderid, dxhy_match_status, source_system, detail_yesorno, uuid, jvcode, gf_name, invoice_type, gf_tax_no, check_code, xf_name, xf_tax_no, flow_type, create_date, invoice_status, tp_status, company_code
) VALUES ( #{invoiceCode}, #{invoiceNo}, #{invoiceAmount}, #{invoiceDate}, #{totalAmount}, #{taxAmount}, #{taxRate}, #{venderid}, '0', '2', '0', #{uuid}, #{jvcode}, #{gfName}, #{invoiceType}, #{gfTaxno}, #{checkNo}, #{xfName}, #{xfTaxNo}, '1', getdate(), '0', '0', #{companyCode}
)

mapper.update:
UPDATE t_dx_record_invoice WITH(ROWLOCK)
SET tp_status='1'
WHERE matchno=#{matchno}

mapper.select:
