mapper.select:
SELECT
TOP (#{map.limit})
A.* FROM ( SELECT row_number () OVER (ORDER BY id desc) AS rownumber, o.* FROM ( select v.id, v.jv_code jvCode, v.company_code companyCode, v.venderid, v.gf_name gfName, v.xf_name xfName, v.invoice_no invoiceNo, v.invoice_type invoiceType, v.invoice_date invoiceDate, v.invoice_amount invoiceAmount, v.refund_notes refundNotes, v.rebateno, v.rebate_expressno rebateExpressno from t_dx_invoice v WITH(NOLOCK) left join t_dx_record_invoice r WITH(NOLOCK) ON v.uuid=r.uuid
and r.flow_type='1'
and v.venderid like concat('%',concat(#{map.venderid},'%'))
and v.invoice_no like concat('%',concat(#{map.invoiceNo},'%'))
AND convert(varchar(10),v.invoice_date,23) >= #{map.invoiceDate1}
AND convert(varchar(10),v.invoice_date,23) <= #{map.invoiceDate2}
) AS o )A WHERE 1=1
and rownumber >#{map.offset}

select count(1)
from t_dx_invoice v WITH(NOLOCK)
left join t_dx_record_invoice r WITH(NOLOCK) ON v.uuid=r.uuid

    and r.flow_type='1'

        and v.venderid like concat('%',concat(#{map.venderid},'%'))


        and v.invoice_no like concat('%',concat(#{map.invoiceNo},'%'))


         AND convert(varchar(10),v.invoice_date,23) >= #{map.invoiceDate1} 


         AND convert(varchar(10),v.invoice_date,23)  <= #{map.invoiceDate2}

select
o.orgtype
from t_ac_org o WITH(NOLOCK)
left join t_ac_user r WITH(NOLOCK) ON o.orgid=r.orgid
where r.userid=#{map.userid}
