mapper.select:

    select top (#{limit}) a.*
    from
    (select row_number() over(order by o.upload_date desc) as rownumber,o.*
    from(

SELECT
*
from t_dx_annoucement_pfr t WITH(NOLOCK)
WHERE t.is_release=0

    and vender_id like  '%'+#{venderId}+'%'


     AND convert(varchar(100),upload_date,23) >= #{createStartDate} 


     AND convert(varchar(100),upload_date,23)  <= #{createEndDate} 



    )as o
    )a
    where rownumber>#{offset};

SELECT COUNT(*)
FROM t_dx_annoucement_pfr t WITH(NOLOCK)
WHERE t.is_release=0

    and vender_id like  '%'+#{venderId}+'%'


     AND convert(varchar(100),upload_date,23) >= #{createStartDate} 


     AND convert(varchar(100),upload_date,23)  <= #{createEndDate}

select *
from t_dx_annoucement_pfr_failure WITH(NOLOCK)
where 1=1

    and  create_by =#{userCode}
