mapper.select:

    SELECT
    TOP (#{limit}) A.*
    FROM
    (
    SELECT
    row_number () OVER (ORDER BY file_id desc) AS rownum, o.*
    FROM
    (

select file_id, file_size,file_name,file_extension,upload_date,vender_type,file_path
from t_common_knowledge_file where 1=1

    and vender_type = #{venderType}


    and file_name like concat('%',concat(#{fileName},'%'))


    and file_extension like concat('%',concat(#{fileExtension},'%'))


     AND convert(varchar(100),upload_date,23) >= #{createStartDate} 


     AND convert(varchar(100),upload_date,23)  <= #{createEndDate} 


    )
    AS o
    )A
    WHERE
    rownum >#{offset}

select count(1)
from t_common_knowledge_file where 1=1

    and vender_type = #{venderType}


    and file_name like concat('%',concat(#{fileName},'%'))


    and file_extension like concat('%',concat(#{fileExtension},'%'))


     AND convert(varchar(100),upload_date,23) >= #{createStartDate} 


     AND convert(varchar(100),upload_date,23)  <= #{createEndDate}
