mapper.select:
/**mycat:schema=${schemaLabel}*/
SELECT TOP (#{entity.limit}) A.* FROM ( SELECT row_number() OVER (ORDER BY o.roleid asc) AS rownumber, o.* FROM(
select r.*, (select d.orgname from t_ac_org d WITH(NOLOCK) where d.orgid = r.orgid) orgName from t_ac_role r
and r.rolename like concat(concat('%',#{entity.rolename}),'%')
and r.orgid=#{entity.orgid}
and r.rolecode like concat(concat('%',#{entity.rolecode}),'%')
and r.create_by = #{entity.createBy}
)as o)as A WHERE rownumber > #{entity.offset}

/**mycat:schema=${schemaLabel}*/ select count(*) from t_ac_role r


		and r.rolename like concat(concat('%',#{entity.rolename}),'%')


		and r.orgid=#{entity.orgid}


		and r.rolecode=#{entity.rolecode}

	 and r.create_by = #{entity.createBy}

/**mycat:schema=${schemaLabel}*/ select count(*) from t_ac_role


		orgid IN

			#{items}

mapper.update:
/**mycat:schema=${schemaLabel}*/ update t_ac_role
rolename = #{entity.rolename}, rolecode = #{entity.rolecode}, orgid = #{entity.orgid}, roledesc = #{entity.roledesc}, roletype = #{entity.roletype}, last_modify_time = #{entity.lastModifyTime}, last_modify_by = #{entity.lastModifyBy},
where roleid = #{entity.roleid}
