mapper.update:
		/**mycat:schema=${schemaLabel}*/ 
		update t_ac_user_billtype   set  isdefault = ''
        where userid = 
 		 (select a.userid from (select userid from t_ac_user_billtype where id = #{entity.id} ) a) 
 		 and id != #{entity.id}

mapper.select:
/**mycat:schema=${schemaLabel}*/ 
SELECT t.* from t_ac_billtype  t  join t_ac_user_billtype ta on t.billtypeid = ta.billtypeid


    AND ta.userid = #{entity.userid}



order by t.orgid asc

    limit #{entity.offset}, #{entity.limit}

/**mycat:schema=${schemaLabel}*/ 
SELECT count(*) from t_ac_billtype  t  join t_ac_user_billtype ta on t.billtypeid = ta.billtypeid


    AND ta.billtypeid in

        #{item}

/**mycat:schema=${schemaLabel}*/ select b.* ,
t.id AS userBilltypeId,
t.isdefault isdefault
FROM t_ac_user_billtype t
LEFT JOIN t_ac_billtype b ON b.billtypeid=t.billtypeid
WHERE t.userid=#{entity.userid}


    and b.`billtypename` like concat('%',#{entity.billtypename},'%')


    and b.`billtypecode` like concat('%',#{entity.billtypecode},'%')


    limit #{entity.offset}, #{entity.limit}

/**mycat:schema=${schemaLabel}*/ select count(*)
FROM t_ac_user_billtype t
LEFT JOIN t_ac_billtype b ON b.billtypeid=t.billtypeid
WHERE t.userid=#{entity.userid}


    and b.`billtypename` like concat('%',#{entity.billtypename},'%')


    and b.`billtypecode` like concat('%',#{entity.billtypecode},'%')

/**mycat:schema=${schemaLabel}*/ select * from (SELECT o.company  company , t.* from t_ac_billtype  t join t_ac_org o WITH(NOLOCK) on  t.orgid = o.orgid) t 
 where 1=1 and t.company = #{userBilltypeEntity.company}


    AND t.billtypeid not IN

        #{itemm}



    AND t.billtypename like concat('%',#{userBilltypeEntity.billtypename},'%')


    AND t.billtypecode like concat('%',#{userBilltypeEntity.billtypecode},'%')


    limit #{userBilltypeEntity.offset}, #{userBilltypeEntity.limit}

/**mycat:schema=${schemaLabel}*/ select count(*) from (SELECT o.company  company , t.* from t_ac_billtype  t join t_ac_org o WITH(NOLOCK) on  t.orgid = o.orgid) t 
 where 1=1 and t.company = #{userBilltypeEntity.company}



    AND t.billtypeid not IN

        #{itemm}




