mapper.select:
select * from (
select id, vender_id, cost_no, gf_tax_no, gf_name,
cost_amount, covered_amount, cost_amount-covered_amount uncoveredAmount, remark,
belongs_to, service_type, payment_mode, pay_day, contract, has_invoice, urgency,
eps_no, instance_id , eps_id, staff_no,
ROW_NUMBER() OVER(Order by id) AS rownum
from t_dx_settlement_match where cost_amount-covered_amount>0

    and cost_no like '%'+#{costNo}+'%'


    and vender_id = #{venderId}


    and eps_no = #{epsNo}

) a where rownum>#{offset} and rownum<=#{offset}+#{limit}

select count(1)
from t_dx_settlement_match where cost_amount-covered_amount>0

    and cost_no like '%'+#{costNo}+'%'


    and vender_id = #{venderId}


    and eps_no= #{epsNo}

select id, cost_type, cost_type_name, cost_time, cost_amount, cost_use, cost_dept, cost_dept_id, covered_amount, cost_amount-covered_amount uncoveredAmount, instance_id, bpms_id, project_code from t_dx_settlement_match_detail where cost_no in
#{item}
and cost_amount-covered_amount>0
