mapper.select:
select * from (
select id, vender_id,cost_type_name,cost_type,is_contract,
ROW_NUMBER() OVER(Order by id desc) AS rownum
from t_dx_cost_type where 1=1

    and vender_id = #{venderId}


    and cost_type = #{costType}


    and cost_type_name like '%'+#{costTypeName}+'%'


) a where rownum>#{offset} and rownum<=#{offset}+#{limit}

select count(1)
from t_dx_cost_type where 1=1

    and vender_id = #{venderId}


    and cost_type = #{costType}


    and cost_type_name like '%'+#{costTypeName}+'%'

mapper.update:
 update t_dx_cost_type

    vender_id = #{entity.venderId},
    cost_type = #{entity.costType},
    cost_type_name = #{entity.costTypeName},
    is_contract= #{entity.isContract}

where id = #{entity.id}

mapper.select:
select count(*) from t_dx_cost_type
where cost_type=#{entity.costType}
and vender_id = #{entity.venderId}
and id !=  #{entity.id}

select count(*) from t_dx_cost_type
where cost_type=#{entity.costType}
and vender_id =  #{entity.venderId}
