mapper.select:
select * from (
select id, jvcode, jvname, gf_tax_no, gf_name, venderid, vender_name,
invoice_amount, invoice_count, order_amount, order_count, match_date,
settlement_method,
ROW_NUMBER() OVER(Order by match_date) AS rownum
from t_dx_fixed_assets_match m  WITH(NOLOCK) where match_status='0' and check_status is null
and settlement_method!='0' and venderid=#{venderid}

    and exists (
    select 1 from
    (select * from t_dx_fixed_assets_link l where l.doc_type='2' and l.match_id=m.id) x
    join t_dx_record_invoice y on x.doc_id=y.id
    where y.invoice_no like '%'+#{invoiceNo}+'%'
    )


    and exists (
    select 1 from
    (select * from t_dx_fixed_assets_link l where l.doc_type='1' and l.match_id=m.id) x
    join t_dx_fixed_assets_order y on x.doc_id=y.id
    where y.order_no like '%'+#{orderNo}+'%'
    )


     AND convert(varchar(10),match_date,23) >= #{matchDate1} 


     AND convert(varchar(10),match_date,23) <= #{matchDate2} 

) a where rownum>#{offset} and rownum<=#{offset}+#{limit}

select count(1) from t_dx_fixed_assets_match m WITH(NOLOCK) where match_status='0' and check_status is null and settlement_method!='0' and venderid=#{venderid}
and exists ( select 1 from (select * from t_dx_fixed_assets_link l where l.doc_type='2' and l.match_id=m.id) x join t_dx_record_invoice y on x.doc_id=y.id where y.invoice_no like '%'+#{invoiceNo}+'%' )
and exists ( select 1 from (select * from t_dx_fixed_assets_link l where l.doc_type='1' and l.match_id=m.id) x join t_dx_fixed_assets_order y on x.doc_id=y.id where y.order_no like '%'+#{orderNo}+'%' )
AND convert(varchar(10),match_date,23) >= #{matchDate1}
AND convert(varchar(10),match_date,23) <= #{matchDate2}

select b.id, b.venderid, b.jvcode, b.order_no, b.order_detail_no, b.order_date, b.amount,b.shamount
from t_dx_fixed_assets_link a  WITH(NOLOCK) join t_dx_fixed_assets_order b on a.doc_id=b.id
where a.doc_type='1' and  a.match_id=#{matchId}
