mapper.select:
SELECT
TOP (#{map.limit})
A.* FROM ( SELECT row_number () OVER (ORDER BY o.id ) AS rownumber ,o.* FROM ( select distinct m.id , m.redticket_data_serial_number redTicketDataSerialNumber, m.business_type businessType, m.red_total_amount redTotalAmount, m.whether_upload_data dataStatus, m.whether_upload_rednotice noticeStatus, m.red_notice_number redNoticeNumber, m.audit_results examineResult, m.audit_remarks examineRemarks, m.data_association dataAssociation, m.tax_rate taxRate, m.rednotice_association redNoticeAssociation from t_dx_red_ticket_matching m WITH(NOLOCK)
left join t_dx_return_agreement_middle tam WITH(NOLOCK) on m.id=tam.red_ticket_matching_association LEFT JOIN t_dx_all_record_invoice_detail tg WITH(NOLOCK) on tg.id = tam.return_agreement_association
left join t_dx_return_agreement_middle tam WITH(NOLOCK) on m.id=tam.red_ticket_matching_association LEFT JOIN t_dx_protocol ta WITH(NOLOCK) on ta.id = tam.return_agreement_association
and m.redticket_status='0' and m.audit_results != '2'
and tg.return_goods_code like concat('%',concat(#{map.serviceNo},'%'))
and ta.protocol_no like concat('%',concat(#{map.serviceNo},'%'))
and m.venderid like concat('%',concat(#{map.userCode},'%'))
and m.business_type = #{map.businessType}
AND convert(varchar(100),m.redticket_creation_time,23) >= #{map.invoiceDate1}
AND convert(varchar(10),m.redticket_creation_time,23) <= #{map.invoiceDate2}
) AS o ) A WHERE 1=1
and rownumber > #{map.offset}

select
count(1)
from (
select
distinct m.id
from t_dx_red_ticket_matching m WITH(NOLOCK)

    left join t_dx_return_agreement_middle  tam  WITH(NOLOCK)  on m.id=tam.red_ticket_matching_association
    LEFT JOIN  t_dx_all_record_invoice_detail  tg  WITH(NOLOCK) on  tg.id = tam.return_agreement_association




    left join t_dx_return_agreement_middle  tam  WITH(NOLOCK)  on m.id=tam.red_ticket_matching_association
    LEFT JOIN  t_dx_protocol  ta WITH(NOLOCK)  on  ta.id = tam.return_agreement_association



    and m.redticket_status='0'
    and m.audit_results ='1'



                and tg.return_goods_code like concat('%',concat(#{map.serviceNo},'%'))





                and ta.protocol_no like concat('%',concat(#{map.serviceNo},'%'))






        and  m.venderid   like concat('%',concat(#{map.userCode},'%'))


        and m.business_type = #{map.businessType}


         AND convert(varchar(100),m.redticket_creation_time,23) >= #{map.invoiceDate1} 


         AND convert(varchar(10),m.redticket_creation_time,23)  <= #{map.invoiceDate2} 



) a
