mapper.select:
/**mycat:schema=${schemaLabel}*/
SELECT TOP (#{map.limit}) A.* FROM ( SELECT row_number () OVER (ORDER BY venderid desc, scanId desc) AS rownumber, o.* FROM (
select a.id, convert(varchar(100),a.create_date,23) createDate, a.invoice_serial_no invoiceSerialNo, a.venderid venderId, a.invoice_type invoiceType, a.invoice_code invoiceCode, a.invoice_no invoiceNo, a.invoice_date invoiceDate, a.invoice_amount invoiceAmount, a.tax_amount taxAmount, a.refund_reason refundReason, a.flow_type flowType, a.scan_id scanId from t_dx_invoice a WITH(NOLOCK) where (a.rebateyesorno != '1' or a.rebateyesorno is null) and a.isdel = '1'
and a.flow_type = #{map.flowType}
and a.flow_type !='2'
and a.venderid = #{map.venderId}
and a.invoice_no = #{map.invoiceNo}
and a.scan_id = #{map.scanId}
AND convert(varchar(100),a.del_date,23) >= #{map.createDate1}
AND convert(varchar(100),a.del_date,23) <= #{map.createDate2}
ORDER BY a.create_date desc, id desc
) AS o )A WHERE rownumber >#{map.offset}

/**mycat:schema=${schemaLabel}*/
select count(1) totalCount, ISNULL(sum(a.invoice_amount),0) totalAmount, ISNULL(sum(a.tax_amount),0) totalTax
from t_dx_invoice a WITH(NOLOCK)
where (a.rebateyesorno != '1' or a.rebateyesorno is null)
and a.isdel = '1'


    and a.flow_type = #{map.flowType}


    and a.flow_type !='2'


    and a.venderid = #{map.venderId}


    and a.invoice_no = #{map.invoiceNo}


    and a.scan_id = #{map.scanId}


     AND convert(varchar(100),a.del_date,23) >= #{map.createDate1} 


     AND convert(varchar(100),a.del_date,23)  <= #{map.createDate2}

select
a.id,
convert(varchar(100),a.create_date,23) createDate,
a.invoice_serial_no invoiceSerialNo,
a.venderid venderId,
a.invoice_type invoiceType,
a.invoice_code invoiceCode,
a.invoice_no invoiceNo,
a.invoice_date invoiceDate,
a.invoice_amount invoiceAmount,
a.tax_amount taxAmount,
a.refund_reason refundReason,
a.flow_type flowType,
a.scan_id scanId
from t_dx_invoice a WITH(NOLOCK)
where (a.rebateyesorno != '1' or a.rebateyesorno is null)
and a.isdel = '1'


    and a.flow_type = #{map.flowType}


    and a.venderid = #{map.venderId}


    and a.invoice_no = #{map.invoiceNo}


    and a.scan_id = #{map.scanId}


     AND convert(varchar(100),a.del_date,23) >= #{map.createDate1} 


     AND convert(varchar(100),a.del_date,23)  <= #{map.createDate2}

mapper.update:
update t_dx_invoice WITH(ROWLOCK)
rebateno =#{rebateNo}, rebateyesorno = '1', rebate_date = getdate()
where id = #{id}

update t_dx_record_invoice WITH(ROWLOCK)
qs_status='0', scan_match_status='0', qs_date=null, scan_match_date=null, tp_status = '2'
where uuid = #{uuid}

mapper.select:
select uuid
from t_dx_invoice WITH(NOLOCK)
where id =#{id}

select rebateno rebateNo
from t_dx_invoice WITH(NOLOCK)
where id =
    #{id}
