mapper.select:
        /**mycat:schema=${schemaLabel}*/
        SELECT
        count(1)
        from t_dx_invoice t WITH(NOLOCK)
        left join t_dx_record_invoice tdri WITH(NOLOCK) on tdri.uuid=t.uuid
        where
        t.valid = 1 and
        t.rebateyesorno = 0 AND
        t.qs_type=1
		AND t.flow_type != '2'



            and (t.dy_invoice_no like concat('%',concat(#{query.invoiceNo},'%')) or t.invoice_no like concat('%',concat(#{query.invoiceNo},'%')))


             AND FORMAT(t.create_date ,'yyyy-MM-dd', 'zh-cn') >= #{query.invoiceDate1} 


             AND FORMAT(t.create_date ,'yyyy-MM-dd', 'zh-cn')  <= #{query.invoiceDate2} 



            and t.scan_id like concat('%',concat(#{query.scanId},'%'))


            and t.venderid = #{query.venderid}


            and t.company_code = #{query.companyCode}


            and t.jv_code = #{query.shName}


            and t.qs_status= #{query.qsStatus}


            and t.flow_type= #{query.flowType}


            and tdri.scan_match_status= #{query.macthStatus}


             and DATEDIFF(DAY, t.invoice_date, GETDATE())>=330


             and DATEDIFF(DAY, t.invoice_date, GETDATE())<330


            and tdri.is_gl= #{query.gl}


            and (tdri.rzh_yesorno= #{query.rzhYesorno} or tdri.rzh_yesorno is null)


            and (tdri.rzh_yesorno= #{query.rzhYesorno})

/**mycat:schema=${schemaLabel}*/
SELECT TOP (#{query.limit}) A.* FROM ( SELECT row_number () OVER (ORDER BY scanSubId,scanRightId asc) AS rownumber, o.* FROM (
SELECT
RIGHT(t.scan_id,17) scanRightId, SUBSTRING(t.scan_id,0,CHARINDEX('_', t.scan_id)) scanSubId, t.id, DATEDIFF(DAY, t.invoice_date, GETDATE()) jpl, t.invoice_code invoiceCode, t.invoice_no invoiceNo, t.dy_invoice_code dyInvoiceCode, t.dy_invoice_no dyInvoiceNo, t.invoice_date invoiceDate, t.invoice_type invoiceType, t.qs_type qsType, t.gf_name gfName, t.xf_name xfName, t.gf_tax_no gfTaxNo, t.xf_tax_no xfTaxNo, t.user_account, t.invoice_amount invoiceAmount, t.tax_amount taxAmount, t.qs_date signInDate, t.qs_status qsStatus, t.notes notes,t.scan_id scanId, t.invoice_serial_no localTrmSeqNum, t.vendername,t.rebateyesorno, t.check_code,t.total_amount,t.create_date, t.venderid,t.jv_code,t.company_code, t.file_type,tdri.rzh_date,tdri.rzh_yesorno,t.flow_type,t.isdel, tdri.scan_match_status scanMatchStatus, tdri.scan_fail_reason scanFailReason, tdri.remark, tdri.is_gl gl, t.venderid_edit venderidEdit,t.is_exist_stamper isExistStamper,t.no_exist_stamper_notes noExistStamperNotes from t_dx_invoice t WITH(NOLOCK) left join t_dx_record_invoice tdri WITH(NOLOCK) on tdri.uuid=t.uuid where t.valid = 1 and t.rebateyesorno = 0 and t.qs_type=1 and t.flow_type != '2'
and (t.dy_invoice_no like concat('%',concat(#{query.invoiceNo},'%')) or t.invoice_no like concat('%',concat(#{query.invoiceNo},'%')))
AND FORMAT(t.create_date ,'yyyy-MM-dd', 'zh-cn') >= #{query.invoiceDate1}
AND FORMAT(t.create_date ,'yyyy-MM-dd', 'zh-cn') <= #{query.invoiceDate2}
and t.scan_id like concat('%',concat(#{query.scanId},'%'))
and t.venderid = #{query.venderid}
and t.company_code = #{query.companyCode}
and t.jv_code = #{query.shName}
and t.qs_status= #{query.qsStatus}
and t.flow_type= #{query.flowType}
and tdri.scan_match_status= #{query.macthStatus}
and DATEDIFF(DAY, t.invoice_date, GETDATE())>=330
and DATEDIFF(DAY, t.invoice_date, GETDATE())<330
and tdri.is_gl= #{query.gl}
and (tdri.rzh_yesorno= #{query.rzhYesorno} or tdri.rzh_yesorno is null)
and (tdri.rzh_yesorno= #{query.rzhYesorno})
) AS o )A WHERE rownumber >#{query.offset} order by scanSubId,scanRightId asc

/**mycat:schema=${schemaLabel}*/
SELECT
SUM(invoice_amount) sumTotalAmount,
SUM(tax_amount) sumTaxAmount
from t_dx_invoice t WITH(NOLOCK)
where t.qs_status=1
and valid = 1



    and t.invoice_no like concat('%',concat(#{query.invoiceNo},'%'))


     AND FORMAT(t.create_date ,'yyyy-MM-dd', 'zh-cn') >= #{query.invoiceDate1} 


     AND FORMAT(t.create_date ,'yyyy-MM-dd', 'zh-cn')  <= #{query.invoiceDate2}

/**mycat:schema=${schemaLabel}*/ SELECT RIGHT(t.scan_id,17) scanRightId, SUBSTRING(t.scan_id,0,CHARINDEX('_', t.scan_id)) scanSubId, t.id, t.invoice_code invoiceCode, t.invoice_no invoiceNo, t.dy_invoice_code dyInvoiceCode, t.dy_invoice_no dyInvoiceNo, t.invoice_date invoiceDate, t.invoice_type invoiceType, t.qs_type qsType, t.gf_name gfName, t.xf_name xfName, t.gf_tax_no gfTaxNo, t.xf_tax_no xfTaxNo, t.user_account, t.invoice_amount invoiceAmount, t.tax_amount taxAmount, t.qs_date signInDate, t.qs_status qsStatus, t.notes notes,t.scan_id scanId, t.invoice_serial_no localTrmSeqNum, t.vendername,t.rebateyesorno, t.check_code,t.total_amount,t.create_date, t.venderid,t.jv_code,t.company_code,t.file_type,tdri.rzh_date,tdri.rzh_yesorno,t.flow_type,t.isdel, tdri.scan_match_status scanMatchStatus, tdri.scan_fail_reason scanFailReason from t_dx_invoice t WITH(NOLOCK) left join t_dx_record_invoice tdri WITH(NOLOCK) on tdri.uuid=t.uuid where t.valid = 1 and t.rebateyesorno = 0 AND t.qs_type=1 AND t.flow_type != '2'
and (t.dy_invoice_no like concat('%',concat(#{query.invoiceNo},'%')) or t.invoice_no like concat('%',concat(#{query.invoiceNo},'%')))
AND FORMAT(t.create_date ,'yyyy-MM-dd', 'zh-cn') >= #{query.invoiceDate1}
AND FORMAT(t.create_date ,'yyyy-MM-dd', 'zh-cn') <= #{query.invoiceDate2}
and t.scan_id like concat('%',concat(#{query.scanId},'%'))
and t.venderid = #{query.venderid}
and t.company_code = #{query.companyCode}
and t.jv_code = #{query.shName}
and t.qs_status= #{query.qsStatus}
and t.flow_type= #{query.flowType}
and tdri.scan_match_status= #{query.macthStatus}
order by scanSubId,scanRightId asc

/**mycat:schema=${schemaLabel}*/
select t.matchid
from t_dx_match_po_claim_invoice t
LEFT JOIN t_dx_record_invoice d WITH(NOLOCK) ON t.code = d.id
where d.uuid=#{uuid}
AND t.code_type = '0';

/**mycat:schema=${schemaLabel}*/
SELECT
    *
FROM
    t_dx_settlement_invoice WITH(NOLOCK)
WHERE
    invoice_code = #{invoiceCode}
    and invoice_no=#{invoiceNo}

/**mycat:schema=${schemaLabel}*/
SELECT
    t.*
FROM
    t_dx_red_ticket_matching t
    LEFT JOIN t_dx_record_invoice d WITH(NOLOCK) ON t.red_notice_number = d.red_notice_number
WHERE
    d.invoice_code = #{invoiceCode}
    and d.invoice_no=#{invoiceNo}

/**mycat:schema=${schemaLabel}*/
SELECT
    serial_number
FROM
    t_dx_redInvoice_detail WITH(NOLOCK)
WHERE
invoice_code = #{invoiceCode}
and invoice_no=#{invoiceNo}

mapper.update:
update t_dx_record_invoice WITH(ROWLOCK) SET is_gl=#{query.glx} where uuid in(
   SELECT
   t.uuid
   from t_dx_invoice t WITH(NOLOCK)
   left join t_dx_record_invoice tdri WITH(NOLOCK) on tdri.uuid=t.uuid
   where
   t.valid = 1 and
   t.rebateyesorno = 0 AND
   t.qs_type=1
   AND t.flow_type != '2'



       and (t.dy_invoice_no like concat('%',concat(#{query.invoiceNo},'%')) or t.invoice_no like concat('%',concat(#{query.invoiceNo},'%')))


        AND FORMAT(t.create_date ,'yyyy-MM-dd', 'zh-cn') >= #{query.invoiceDate1} 


        AND FORMAT(t.create_date ,'yyyy-MM-dd', 'zh-cn')  <= #{query.invoiceDate2} 



       and t.scan_id like concat('%',concat(#{query.scanId},'%'))


       and t.venderid = #{query.venderid}


       and t.company_code = #{query.companyCode}


       and t.jv_code = #{query.shName}


       and t.qs_status= #{query.qsStatus}


       and t.flow_type= #{query.flowType}


       and tdri.scan_match_status= #{query.macthStatus}


        and DATEDIFF(DAY, t.invoice_date, GETDATE())>=330


        and DATEDIFF(DAY, t.invoice_date, GETDATE())<330


       and tdri.is_gl= #{query.gl}

)
